mapper.select:
select c.*, f.foodId as ffoodId, f.foodName as ffoodName, f.foodExplain as ffoodExplain, f.foodImg as ffoodImg, f.foodPrice as ffoodPrice, f.businessId as fbusinessId, f.remarks as fremarks, b.businessId as bbusinessId, b.businessName as bbusinessName, b.businessAddress as bbusinessAddress, b.businessExplain as bbusinessExplain, b.businessImg as bbusinessImg, b.orderTypeId as borderTypeId, b.starPrice as bstarPrice, b.deliveryPrice as bdeliveryPrice, b.remarks as bremarks from (cart c left join food f on c.foodId = f.foodId) left join business b on c.businessId = b.businessId
c.userId=#{userId}
and c.businessId=#{businessId}
order by cartId

mapper.delete:
delete from cart 

userId=#{userId} and businessId=#{businessId}

and foodId=#{foodId}
